let article = "";

// Lấy phần introduction từ node "Blog Planner"
article += $('Blog Planner').first().json.output.introduction;
article += "\n\n";

// Duyệt qua từng chương từ các node đầu vào (chứa cả tiêu đề, nội dung và ảnh)
for (const item of $input.all()) {
  const title = item.json.chapter_title || "Chapter";
  const content = item.json.chapter_content || "";
  const imageUrl = item.json.source_url || "";

  // Thêm tiêu đề chương
  article += `## ${title}\n\n`;

  // Thêm hình ảnh nếu có
  if (imageUrl !== "") {
    article += `![${title}](${imageUrl})\n\n`;
  }

  // Thêm nội dung chương
  article += content + "\n\n";
}

// Thêm phần kết luận (Conclusion)
article += "###Kết Luận\n\n";
article += $('Blog Planner').first().json.output.conclusion;
article += "\n\n";

// Thêm phần kêu gọi hành động (CTA)
article += $('Blog Planner').first().json.output.cta + "\n\n";
article += "Xem thêm: [" + $('Blog Planner').first().json.output.cta_url + "](" + $('Blog Planner').first().json.output.cta_url + ")\n\n";

// Thêm phần giới thiệu công ty (Company Offering)
article += "###Giới thiệu doanh nghiệp\n\n";
article += $('Blog Planner').first().json.output.company_offering;

// Xuất kết quả
return [
  {
    article
  }
];
