let article = "";

// Introduction
article += $('Blog Planner').first().json.output.introduction;
article += "\n\n";

for (const item of $input.all()) {
  // Add the chapter title as a level 2 Markdown header
  article += "## " + item.json.chapter_title + "\n\n";

  // Insert the chapter image if it exists (Markdown image syntax)
  if (item.json.source_url) {
    article += `![${item.json.title.rendered}](${item.json.source_url})\n\n`;
  }

  // Add the chapter content
  article += item.json.chapter_content;
  article += "\n\n";
}

// Conclusion
article += "## Final words\n\n";
article += $('Blog Planner').first().json.output.conclusion;
article += "\n\n";

// Call-to-Action (CTA)
article += $('Blog Planner').first().json.output.cta + "\n\n";
article += "Learn more: [" + $('Blog Planner').first().json.output.ctaUrl + "](" + $('Blog Planner').first().json.output.ctaUrl + ")\n\n";

// Company Offering
article += "## About us\n\n";
article += $('Blog Planner').first().json.output.companyOffering;

return [
  {
    "article": article
  }
];
